import React from 'react';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';

const Games = () => {
  const games = [
    {
      id: 1,
      title: "Climate Impact Simulator",
      description: "Make decisions as a world leader and see how your choices impact global climate over decades",
      image: "climate-simulator.jpg",
      path: "/games/climate-simulator"
    },
    {
      id: 2,
      title: "Ecosystem Balance",
      description: "Balance different elements of an ecosystem to maintain biodiversity while adapting to climate change",
      image: "ecosystem-balance.jpg",
      path: "/games/ecosystem-balance"
    },
    {
      id: 3,
      title: "Climate Hero Journey",
      description: "Navigate through real-world scenarios and make sustainable choices to become a climate hero",
      image: "climate-hero.jpg",
      path: "/games/climate-hero"
    }
  ];

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Climate Games</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {games.map((game) => (
            <Link 
              key={game.id} 
              to={game.path}
              className="transform transition duration-300 hover:scale-105"
            >
              <div className="bg-white rounded-lg shadow-lg overflow-hidden">
                <div className="h-48 bg-gray-200">
                  {/* Placeholder for game image */}
                  <div className="w-full h-full flex items-center justify-center bg-primary-100">
                    <span className="text-primary-600 text-lg font-semibold">{game.title}</span>
                  </div>
                </div>
                <div className="p-6">
                  <h2 className="text-xl font-semibold text-gray-900 mb-2">{game.title}</h2>
                  <p className="text-gray-600">{game.description}</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Games;
