import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Rewards = () => {
  const [rewards, setRewards] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRewards = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/redemption/items', {
          headers: { Authorization: `Bearer ${token}` }
        });

        if (Array.isArray(response.data)) {
          // Remove duplicates and group by category
          const uniqueRewards = response.data.reduce((acc, current) => {
            const x = acc.find(item => item.id === current.id);
            if (!x) {
              return acc.concat([current]);
            } else {
              return acc;
            }
          }, []);

          setRewards(uniqueRewards);
        }
      } catch (error) {
        console.error('Error fetching rewards:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchRewards();
  }, []);

  // Group rewards by category
  const groupedRewards = rewards.reduce((acc, reward) => {
    if (!acc[reward.category]) {
      acc[reward.category] = [];
    }
    acc[reward.category].push(reward);
    return acc;
  }, {});

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-2xl font-semibold mb-6">Rewards & Achievements</h2>
      {loading ? (
        <div className="flex justify-center items-center h-32">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-green-600"></div>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {Object.entries(groupedRewards).map(([category, categoryRewards]) => (
            <div key={category} className="border rounded-lg p-4 bg-gradient-to-br from-green-50 to-blue-50">
              <h3 className="text-xl font-semibold mb-4 capitalize">{category}</h3>
              <div className="space-y-3">
                {categoryRewards.map(reward => (
                  <div key={reward.id} className="flex items-start p-3 bg-white rounded-lg shadow-sm">
                    <div className="flex-grow">
                      <h4 className="font-medium text-gray-800">{reward.name}</h4>
                      <p className="text-sm text-gray-600">{reward.description}</p>
                    </div>
                    <div className="ml-4">
                      <span className="text-sm font-semibold text-green-600">
                        {reward.points_required} pts
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Rewards;
