import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';
import axios from 'axios';
import { format } from 'date-fns';

const AdminDashboard = () => {
  const [eventRegistrations, setEventRegistrations] = useState([]);
  const [stats, setStats] = useState(null);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('events');
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    if (activeTab === 'events') {
      fetchEventRegistrations();
    } else if (activeTab === 'users') {
      fetchUsers(currentPage);
      fetchStats();
    }
  }, [activeTab, currentPage]);

  const fetchEventRegistrations = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/admin/event-registrations', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setEventRegistrations(response.data);
      setError('');
    } catch (err) {
      console.error('Error fetching registrations:', err);
      setError('Failed to load event registrations');
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/admin/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStats(response.data);
    } catch (err) {
      console.error('Error fetching stats:', err);
    }
  };

  const fetchUsers = async (page) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`/api/admin/users?page=${page}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(response.data);
    } catch (err) {
      console.error('Error fetching users:', err);
      setError('Failed to load users');
    }
  };

  const updateUserStatus = async (userId, updates) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`/api/admin/users/${userId}`, updates, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchUsers(currentPage); // Refresh user list
    } catch (err) {
      console.error('Error updating user:', err);
      setError('Failed to update user');
    }
  };

  const StatsSection = () => (
    stats && (
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-semibold mb-2">Users</h3>
          <p className="text-2xl font-bold">{stats.users.total_users}</p>
          <p className="text-sm text-gray-500">{stats.users.admin_count} Admins</p>
        </div>
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-semibold mb-2">Events</h3>
          <p className="text-2xl font-bold">{stats.events.total_events}</p>
          <p className="text-sm text-gray-500">
            {stats.events.upcoming_events} Upcoming
          </p>
        </div>
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-semibold mb-2">Registrations</h3>
          <p className="text-2xl font-bold">
            {stats.registrations.total_registrations}
          </p>
          <p className="text-sm text-gray-500">
            {stats.registrations.confirmed_registrations} Confirmed
          </p>
        </div>
      </div>
    )
  );

  const UsersTable = () => (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-2xl font-semibold mb-6">User Management</h2>
      {users.users?.length > 0 ? (
        <>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    User
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Email
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Role
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {users.users.map((user) => (
                  <tr key={user.id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {user.username}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {user.email}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                        user.status === 'active'
                          ? 'bg-green-100 text-green-800'
                          : 'bg-red-100 text-red-800'
                      }`}>
                        {user.status}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {user.isAdmin ? 'Admin' : 'User'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <button
                        onClick={() => updateUserStatus(user.id, { 
                          status: user.status === 'active' ? 'suspended' : 'active' 
                        })}
                        className="text-indigo-600 hover:text-indigo-900 mr-4"
                      >
                        {user.status === 'active' ? 'Suspend' : 'Activate'}
                      </button>
                      <button
                        onClick={() => updateUserStatus(user.id, { 
                          isAdmin: !user.isAdmin 
                        })}
                        className="text-indigo-600 hover:text-indigo-900"
                      >
                        {user.isAdmin ? 'Remove Admin' : 'Make Admin'}
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          {users.pagination && (
            <div className="mt-4 flex justify-center">
              <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px">
                {Array.from({ length: users.pagination.totalPages }, (_, i) => (
                  <button
                    key={i + 1}
                    onClick={() => setCurrentPage(i + 1)}
                    className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                      currentPage === i + 1
                        ? 'z-10 bg-indigo-50 border-indigo-500 text-indigo-600'
                        : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50'
                    }`}
                  >
                    {i + 1}
                  </button>
                ))}
              </nav>
            </div>
          )}
        </>
      ) : (
        <div className="text-center text-gray-500">No users found</div>
      )}
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-8">Admin Dashboard</h1>
        
        <div className="flex space-x-4 mb-6">
          <button
            onClick={() => setActiveTab('events')}
            className={`px-4 py-2 rounded ${
              activeTab === 'events'
                ? 'bg-forest text-white'
                : 'bg-gray-200 text-gray-700'
            }`}
          >
            Events
          </button>
          <button
            onClick={() => setActiveTab('users')}
            className={`px-4 py-2 rounded ${
              activeTab === 'users'
                ? 'bg-forest text-white'
                : 'bg-gray-200 text-gray-700'
            }`}
          >
            User Management
          </button>
        </div>

        {loading ? (
          <div className="text-center py-8">Loading...</div>
        ) : error ? (
          <div className="text-red-600 mb-4">{error}</div>
        ) : activeTab === 'events' ? (
          <EventRegistrationsTable eventRegistrations={eventRegistrations} />
        ) : (
          <>
            <StatsSection />
            <UsersTable />
          </>
        )}
      </div>
    </div>
  );
};

const EventRegistrationsTable = ({ eventRegistrations }) => (
  <div className="bg-white rounded-lg shadow-md p-6">
    <h2 className="text-2xl font-semibold mb-6">Event Registrations</h2>
    {loading ? (
      <div className="text-center">Loading...</div>
    ) : error ? (
      <div className="text-red-600">{error}</div>
    ) : eventRegistrations.length === 0 ? (
      <div className="text-gray-500">No registrations found</div>
    ) : (
      eventRegistrations.map(({ event, registrations }) => (
        <div key={event.id} className="mb-8">
          <h3 className="text-xl font-semibold mb-4">
            {event.title} - {format(new Date(event.date), 'PPP')}
          </h3>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    User
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Email
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Registration Date
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {registrations.map((user) => (
                  <tr key={user.id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {user.full_name || user.username}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {user.email}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {format(new Date(user.registration_date), 'PPP')}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                        user.status === 'confirmed'
                          ? 'bg-green-100 text-green-800'
                          : user.status === 'cancelled'
                          ? 'bg-red-100 text-red-800'
                          : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {user.status}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ))
    )}
  </div>
);

export default AdminDashboard;
