import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RedemptionPortal = () => {
  const [items, setItems] = useState([]);
  const [userPoints, setUserPoints] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showRedemptionModal, setShowRedemptionModal] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [shippingAddress, setShippingAddress] = useState('');

  const categories = [
    { id: 'all', name: 'All Items' },
    { id: 'merchandise', name: 'Merchandise' },
    { id: 'vouchers', name: 'Vouchers' },
    { id: 'experiences', name: 'Experiences' },
    { id: 'donations', name: 'Donations' }
  ];

  useEffect(() => {
    fetchItems();
    fetchUserPoints();
  }, []);

  const fetchItems = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/redemption/items', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setItems(response.data);
    } catch (error) {
      setError('Failed to fetch redemption items');
    }
  };

  const fetchUserPoints = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/users/points', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUserPoints(response.data.points);
    } catch (error) {
      setError('Failed to fetch user points');
    }
  };

  const handleRedeem = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/redemption/redeem', {
        itemId: selectedItem.id,
        shippingAddress
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setSuccess('Item redeemed successfully!');
      setShowRedemptionModal(false);
      fetchUserPoints();
      fetchItems();
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to redeem item');
    }
  };

  const filteredItems = selectedCategory === 'all' 
    ? items 
    : items.filter(item => item.category === selectedCategory);

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-semibold">Redemption Portal</h2>
        <div className="text-green-600 font-semibold">
          Your Points: {userPoints}
        </div>
      </div>

      {/* Category Filter */}
      <div className="flex gap-2 mb-6 overflow-x-auto pb-2">
        {categories.map(category => (
          <button
            key={category.id}
            onClick={() => setSelectedCategory(category.id)}
            className={`px-4 py-2 rounded-full text-sm font-medium ${
              selectedCategory === category.id
                ? 'bg-green-600 text-white'
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
            }`}
          >
            {category.name}
          </button>
        ))}
      </div>

      {/* Items Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredItems.map(item => (
          <div key={item.id} className="border rounded-lg overflow-hidden hover:shadow-lg transition-shadow">
            <img
              src={item.image_url || 'https://via.placeholder.com/300x200'}
              alt={item.name}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h3 className="text-lg font-semibold mb-2">{item.name}</h3>
              <p className="text-gray-600 text-sm mb-4">{item.description}</p>
              <div className="flex justify-between items-center">
                <span className="text-green-600 font-bold">{item.points_required} points</span>
                <button
                  onClick={() => {
                    setSelectedItem(item);
                    setShowRedemptionModal(true);
                  }}
                  disabled={userPoints < item.points_required || item.status !== 'available'}
                  className={`px-4 py-2 rounded-lg text-sm font-medium ${
                    userPoints >= item.points_required && item.status === 'available'
                      ? 'bg-green-600 text-white hover:bg-green-700'
                      : 'bg-gray-100 text-gray-400 cursor-not-allowed'
                  }`}
                >
                  Redeem
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Redemption Modal */}
      {showRedemptionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full m-4">
            <h3 className="text-xl font-semibold mb-4">Confirm Redemption</h3>
            <p className="mb-4">
              Are you sure you want to redeem {selectedItem.name} for {selectedItem.points_required} points?
            </p>
            {selectedItem.category !== 'vouchers' && (
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Shipping Address
                </label>
                <textarea
                  value={shippingAddress}
                  onChange={(e) => setShippingAddress(e.target.value)}
                  className="w-full rounded-md border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                  rows="3"
                  required
                />
              </div>
            )}
            <div className="flex justify-end gap-4">
              <button
                onClick={() => setShowRedemptionModal(false)}
                className="px-4 py-2 text-sm font-medium text-gray-700 hover:text-gray-900"
              >
                Cancel
              </button>
              <button
                onClick={handleRedeem}
                className="px-4 py-2 text-sm font-medium text-white bg-green-600 rounded-lg hover:bg-green-700"
              >
                Confirm Redemption
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RedemptionPortal;
