import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './Navbar';

const RedemptionPortal = () => {
  const [items, setItems] = useState([]);
  const [userPoints, setUserPoints] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showRedemptionModal, setShowRedemptionModal] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [shippingAddress, setShippingAddress] = useState('');
  const [loading, setLoading] = useState(true);
  const [redeemLoading, setRedeemLoading] = useState(false);

  const categories = [
    { id: 'all', name: 'All Items' },
    { id: 'merchandise', name: 'Merchandise' },
    { id: 'vouchers', name: 'Vouchers' },
    { id: 'experiences', name: 'Experiences' },
    { id: 'donations', name: 'Donations' }
  ];

  useEffect(() => {
    fetchItems();
    fetchUserPoints();
  }, []);

  useEffect(() => {
    const pointsInterval = setInterval(fetchUserPoints, 30000); // Refresh every 30 seconds
    return () => clearInterval(pointsInterval);
  }, []);

  const fetchItems = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/redemption/items', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      // Log the response for debugging
      console.log('Items fetched:', response.data);
      
      if (Array.isArray(response.data)) {
        setItems(response.data);
        setError('');
      } else {
        setError('Invalid data format received');
        setItems([]);
      }
    } catch (error) {
      console.error('Error fetching items:', error);
      setError('Failed to fetch redemption items');
      setItems([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchUserPoints = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/users/points', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setUserPoints(response.data.points);
      setError(''); // Clear any existing errors
    } catch (error) {
      console.error('Error fetching points:', error);
      // Don't show the error to user, just set points to 0
      setUserPoints(0);
    }
  };

  const handleRedeem = async () => {
    try {
      setRedeemLoading(true);
      const token = localStorage.getItem('token');
      await axios.post('/api/redemption/redeem', {
        itemId: selectedItem.id,
        shippingAddress
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setSuccess('Item redeemed successfully!');
      setShowRedemptionModal(false);
      setShippingAddress('');
      fetchUserPoints();
      fetchItems();
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to redeem item');
    } finally {
      setRedeemLoading(false);
    }
  };

  const filteredItems = React.useMemo(() => {
    return selectedCategory === 'all' 
      ? items 
      : items.filter(item => item.category === selectedCategory);
  }, [items, selectedCategory]);

  // Add this console log in the render to debug
  console.log('Rendering items:', filteredItems.length);

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-semibold">Rewards Shop</h2>
            <div className="flex items-center bg-green-50 px-4 py-2 rounded-lg">
              <span className="text-green-600 font-semibold">
                {userPoints.toLocaleString()} Points Available
              </span>
            </div>
          </div>

          {/* Category Filter */}
          <div className="flex gap-2 mb-6 overflow-x-auto pb-2">
            {categories.map(category => (
              <button
                key={category.id}
                onClick={() => setSelectedCategory(category.id)}
                className={`eco-button ${
                  selectedCategory === category.id
                    ? 'eco-button-primary'
                    : 'eco-button-secondary'
                }`}
              >
                {category.name}
              </button>
            ))}
          </div>

          {error && (
            <div className="mb-6 p-4 bg-red-100 text-red-700 rounded-lg">
              {error}
            </div>
          )}

          {success && (
            <div className="mb-6 p-4 bg-green-100 text-green-700 rounded-lg">
              {success}
            </div>
          )}

          {loading ? (
            <div className="flex justify-center items-center h-64">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-600"></div>
            </div>
          ) : items.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-gray-600">No items available for redemption</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredItems.map(item => (
                <div 
                  key={item.id} 
                  className="eco-card overflow-hidden"
                >
                  <img
                    src={item.image_url || 'https://via.placeholder.com/300x200?text=Product+Image'}
                    alt={item.name}
                    className="w-full h-48 object-cover"
                    onError={(e) => {
                      e.target.src = 'https://via.placeholder.com/300x200?text=Product+Image';
                    }}
                  />
                  <div className="p-4">
                    <h3 className="text-lg font-semibold mb-2">{item.name}</h3>
                    <p className="text-gray-600 text-sm mb-4">{item.description}</p>
                    <div className="flex justify-between items-center">
                      <div>
                        <span className="text-green-600 font-bold">{item.points_required} points</span>
                        <span className="text-sm text-gray-500 ml-2">
                          {item.stock > 0 ? `${item.stock} left` : 'Out of stock'}
                        </span>
                      </div>
                      <button
                        onClick={() => {
                          setSelectedItem(item);
                          setShowRedemptionModal(true);
                        }}
                        disabled={userPoints < item.points_required || item.stock <= 0}
                        className={`eco-button ${
                          userPoints >= item.points_required && item.stock > 0
                            ? 'eco-button-primary'
                            : 'eco-button-secondary'
                        }`}
                      >
                        {item.stock <= 0 ? 'Out of Stock' : 'Redeem'}
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Redemption Modal */}
          {showRedemptionModal && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white rounded-lg p-6 max-w-md w-full m-4">
                <h3 className="text-xl font-semibold mb-4">Confirm Redemption</h3>
                <p className="mb-4">
                  Are you sure you want to redeem {selectedItem.name} for {selectedItem.points_required} points?
                </p>
                {selectedItem.category !== 'vouchers' && (
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Shipping Address
                    </label>
                    <textarea
                      value={shippingAddress}
                      onChange={(e) => setShippingAddress(e.target.value)}
                      className="w-full rounded-md border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                      rows="3"
                      required
                    />
                  </div>
                )}
                <div className="flex justify-end gap-4">
                  <button
                    onClick={() => setShowRedemptionModal(false)}
                    className="eco-button eco-button-secondary"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleRedeem}
                    disabled={redeemLoading}
                    className={`eco-button eco-button-primary ${
                      redeemLoading ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  >
                    {redeemLoading ? 'Processing...' : 'Confirm Redemption'}
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default RedemptionPortal;
