import React from 'react';

const Rewards = () => {
  const rewardCategories = [
    {
      title: 'Game Achievements',
      icon: 'üéÆ',
      rewards: [
        { name: 'Climate Champion', description: 'Complete all climate games', points: 500 },
        { name: 'Quick Learner', description: 'Complete first game with high score', points: 100 },
        { name: 'Game Master', description: 'Achieve high scores in all games', points: 300 }
      ]
    },
    {
      title: 'Event Participation',
      icon: 'üéØ',
      rewards: [
        { name: 'Event Explorer', description: 'Attend first event', points: 50 },
        { name: 'Active Participant', description: 'Attend 5 events', points: 200 },
        { name: 'Community Leader', description: 'Attend 10 events', points: 400 }
      ]
    },
    {
      title: 'Learning Progress',
      icon: 'üìö',
      rewards: [
        { name: 'Knowledge Seeker', description: 'Complete first learning module', points: 100 },
        { name: 'Environmental Scholar', description: 'Complete all basic modules', points: 300 },
        { name: 'Eco Expert', description: 'Complete all advanced modules', points: 500 }
      ]
    },
    {
      title: 'Community Impact',
      icon: 'üåç',
      rewards: [
        { name: 'Eco Initiator', description: 'Start first community project', points: 200 },
        { name: 'Green Influencer', description: 'Inspire 5 others to join', points: 300 },
        { name: 'Earth Guardian', description: 'Lead a community project', points: 500 }
      ]
    }
  ];

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-2xl font-semibold mb-6">Rewards & Achievements</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {rewardCategories.map((category, index) => (
          <div key={index} className="border rounded-lg p-4 bg-gradient-to-br from-green-50 to-blue-50">
            <div className="flex items-center mb-4">
              <span className="text-2xl mr-2">{category.icon}</span>
              <h3 className="text-xl font-semibold">{category.title}</h3>
            </div>
            <div className="space-y-3">
              {category.rewards.map((reward, rewardIndex) => (
                <div 
                  key={rewardIndex}
                  className="flex items-start p-3 bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow"
                >
                  <div className="flex-grow">
                    <h4 className="font-medium text-gray-800">{reward.name}</h4>
                    <p className="text-sm text-gray-600">{reward.description}</p>
                  </div>
                  <div className="ml-4 flex items-center">
                    <span className="text-sm font-semibold text-green-600">
                      {reward.points} pts
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Rewards;
